mapper.select:
select * from (select row_.*, rownum rownum_ from (select * from (select p.id_, p.rev_, p.category_, p.name_, p.key_, p.version_, p.deployment_id_, p.resource_name_, p.dgrm_resource_name_, p.description_, p.has_graphical_notation_, p.suspension_state_, p.tenant_id_, row_number() OVER(PARTITION BY p.key_ ORDER BY p.version_ desc) as row_flg from act_re_procdef p) temp temp.row_flg = '1' order by version_ desc) row_
rownum <=#{rows}
)
rownum_ >=#{page}
