mapper.select:
		 select (select sum(r.sl)
            from erp_stocks_record r
            r.record_type = 0
             and r.product =#{product} and r.stock=#{stock}
           ) -
         (select nvl(sum(r.sl), 0)
            from erp_stocks_record r
            r.record_type = 1
             and r.product =#{product} and r.stock=#{stock}
           )
   		 from dual

		 select (select sum(r.sl)
            from erp_stocks_record r
            r.record_type = 0
             and r.product =#{product}
           ) -
         (select nvl(sum(r.sl), 0)
            from erp_stocks_record r
            r.record_type = 1
             and r.product =#{product}
           )
   		 from dual
