mapper.select:
select * from (select row_.*, rownum rownum_ from (select * from
erp_stocks_record order by record_Id desc ) row_

	rownum <= #{rows} and record_Type=1
	 and product =#{cp}
	 and stock = #{kw}
	 and jbr = #{jbr}
	 and sl = #{rksl}
	
	    and to_char(sj,'yyyy-MM-dd') > =  #{beginTime}
	
	
	    and to_char(sj,'yyyy-MM-dd') <=  #{endTime}
	

)

	rownum_ >= #{page}

select count(1) from erp_stocks_record

	record_Type=1
	 and product =#{cp}
	 and stock = #{kw}
	 and jbr = #{jbr}
	 and sl = #{rksl}
	
	    and to_char(sj,'yyyy-MM-dd') > =  #{beginTime}
	
	
	    and to_char(sj,'yyyy-MM-dd') <=  #{endTime}
